class CountAndPrint implements Runnable {
    private final String name;

    CountAndPrint(String name) {
        this.name = name;
    }

    static int i = 0;

    @Override
    public void run() {
        while (i < 10) System.out.println(this.name + ": " + i++);
    }

    public static void main(String[] args) {
        for (int t = 1; t <= 4; t++)
            new Thread(new CountAndPrint("Instance " + t)).start();
        while (i < 10) System.out.println("Main: " + i++);
    }
}
